-- 1Ô∏è‚É£ Identified top 10 apps with the highest ratings.
SELECT App, Rating
FROM cleaned_apps_db
ORDER BY Rating DESC
LIMIT 10;

-- 2Ô∏è‚É£ Obtained installs and reviews for the top 5 most reviewed apps.
SELECT App, Installs, Reviews
FROM cleaned_apps_db
ORDER BY Reviews DESC
LIMIT 5;

-- 3Ô∏è‚É£ Found the app with the highest number of reviews and its category.
SELECT App, Reviews, Category
FROM cleaned_apps_db
ORDER BY Reviews DESC
LIMIT 1;

-- 4Ô∏è‚É£ Calculated total revenue generated by the Google Play Store through app purchases.
SELECT SUM(Price * Installs)
FROM cleaned_apps_db;

-- 5Ô∏è‚É£ Determined the category with the highest number of installs and total installs.
SELECT Category, SUM(Installs) AS TOTAL_SUM
FROM cleaned_apps_db
GROUP BY Category
ORDER BY TOTAL_SUM DESC
LIMIT 1;

-- 6Ô∏è‚É£ Analyzed the genre with the most published apps.
SELECT Genres, COUNT(Genres)
FROM cleaned_apps_db
GROUP BY Genres
ORDER BY COUNT(Genres) DESC
LIMIT 1;

-- 7Ô∏è‚É£ Provided a list of games ranked by installs.
SELECT DISTINCT App AS 'Name of the game', Installs
FROM cleaned_apps_db
WHERE Category = 'GAME'
ORDER BY Installs DESC;

-- 8Ô∏è‚É£ Counted the number of free and paid apps.
SELECT Type, COUNT(*) AS count 
FROM cleaned_apps_db
WHERE TYPE IN ('Free', 'Paid')
GROUP BY Type;

-- 9Ô∏è‚É£ Identified the best dating app based on the highest number of reviews.
SELECT App, Reviews
FROM cleaned_apps_db
WHERE Category IN ("Dating")
ORDER BY Reviews DESC
LIMIT 1;

-- üîü Compared reviews with positive and negative sentiment for the app "10 Best Foods for You."
SELECT Sentiment, COUNT(*)
FROM cleaned_reviews_db
WHERE Sentiment IN ("Positive", "Negative") AND App = ("10 Best Foods for You")
GROUP BY Sentiment;

-- 1Ô∏è‚É£1Ô∏è‚É£ Extracted comments for ASUS SuperNote with sentiment polarity and subjectivity both equal to 1.
SELECT App, Translated_Review, Sentiment_Polarity, Sentiment_Subjectivity
FROM cleaned_reviews_db
WHERE Sentiment_Polarity = 1 AND Sentiment_Subjectivity = 1 AND App = "ASUS SuperNote";

-- 1Ô∏è‚É£2Ô∏è‚É£ Extracted neutral sentiment reviews for the app "Abs Training-Burn belly fat."
SELECT App, Translated_Review, Sentiment
FROM cleaned_reviews_db
WHERE APP = "Abs Training-Burn belly fat"  AND Sentiment = "Neutral";

-- 1Ô∏è‚É£3Ô∏è‚É£ Extracted negative sentiment reviews for ASUS SuperNote with sentiment polarity and subjectivity.
SELECT Translated_Review, Sentiment_Polarity, Sentiment_Subjectivity
FROM cleaned_reviews_db
WHERE App = 'ASUS SuperNote'
AND Sentiment = 'Negative';
